        super(props);
        this.state = {
            isCardView: false,
            gericht: {}
        };
    }

    componentDidMount() {
        //https://stackoverflow.com/questions/41110307/pass-props-to-componentdidmount-in-react-component
        const _this = this;

        // Init chart with data from props.
        var chart = Highcharts.chart('chart', {
            series: [{ data: _this.props.data }]
        });

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: chart
        };
        fetch('http://localhost:3000/mygericht', requestOptions)
