import React, { Component } from 'react';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';
import IconButton from '@material-ui/core/IconButton';

function FormatDate(date) {
    var d = new Date(date),
        month = '' + (d.getMonth() + 1),
        day = '' + d.getDate(),
        year = d.getFullYear();

    if (month.length < 2)
        month = '0' + month;
    if (day.length < 2)
        day = '0' + day;

    return [year, month, day].join('-');
}

function isEmpty(date) {
    if (date == null) {
        return false
    } else {
        return true
    }
}

function todayData(dateNow) {
    var dateNow = new Date(dateNow)
    return dateNow
}

class TimeHome extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: 0,
            date: [],
            mymensa: "",
            isLoaded: false
        };
        this.nextDay = this.nextDay.bind(this);
    }

    nextDay(event) {
        this.componentWillMount()

    }

    componentWillMount() {
        this.setState({ isLoaded: true })
        this.setState({ value: this.state.value + 1 });
        var i = this.state.value
        var dateL = new Date('2019,10,11')
        dateL.setDate(dateL.getDate() + i)
        var day_date2 = FormatDate(dateL)
        var safeDate = {"date": day_date2 , "closed": "true"}

        //https://stackoverflow.com/questions/40981040/using-a-fetch-inside-another-fetch-in-javascript
        var result = fetch('/mymensa', {
            method: 'get',
        }).then(function (response) {
            return response.json(); // pass the data as promise to next then block
        }).then(function (data) {
            var canteen_id = data[0].id;
            var date = new Date('2019,10,11')
            date.setDate(date.getDate() + i)
            let day_date = FormatDate(date)


            console.log(canteen_id, '\n' + day_date);
            var url = 'https://openmensa.org/api/v2/canteens/' + canteen_id + '/days/' + day_date
            return fetch(url); // make a 2nd request and return a promise
        })
            .then(function (response) {
                if(response != null){
                    return response.json();}else{
                        return safeDate
                    }
            })
            .catch(function (error) {
                console.log('Request failed test', error)
            })

        result.then(date => this.setState({ date }));
    }

    render() {
        const isEmpty2 = isEmpty(this.state.date)
        const days = ["Mon", "Di", "Mi", "Do", "Fr", "Sa", "So"]
        var day = NaN
        var dayN = NaN

        return (
            <div>
                <p>Ã–ffnungszeiten</p>
                <hr></hr>
                {
                    (isEmpty2) ?
                        <div > {
                            day = new Date(this.state.date.date),
                            dayN = days[day.getDay() - 1]
                            }
                            <p>{this.state.date.date}</p>
                            <div >
                                <IconButton color="secondary" aria-label="add an alarm" onClick={this.nextDay}>
                                    <ArrowForwardIosIcon />
                                </IconButton></div>
                        </div>


                        : <div><h5>{dayN}</h5>
                        <p>{this.state.date.date}</p>
                        <div >
                            <IconButton color="secondary" aria-label="add an alarm" onClick={this.nextDay}>
                                <ArrowForwardIosIcon />
                            </IconButton></div></div>

                }</div>
        )
    }
}
export default TimeHome
