        <IconButton key="tableModeButton"
            onClick={() => {
                this.setState({
                    gericht: g
                })
                this.setState({
                    isCardView: !this.state.isCardView
                })
                if(!this.state.isCardView){
                    postGericht(g)
                }
                
                
            }

            } >
            {this.state.isCardView ? <FavoriteOutlinedIcon /> : <FavoriteBorderOutlinedIcon />}
        </IconButton>) */
