import React, { Component } from "react";
import { Card } from "@material-ui/core";
import CardContent from '@material-ui/core/CardContent';

class NearMensa extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: NaN,
            isLoaded: false,
            url: ""
        };
    }

    localitation() {
        var dataN = null
        if ("geolocation" in navigator) {
            navigator.geolocation.getCurrentPosition(async function (position) {

                var url = 'http://openmensa.org/api/v2/canteens?near[lat]=' + position.coords.latitude + '&near[lng]=' + position.coords.longitude + '&near[dist]=5';
                const response = await fetch(url);
                const data = await response.json()
                dataN = data[0]
                

            });
        } return dataN;
    }

    card() {
        const v = this.localitation()
        return (
            <Card>
                <CardContent>
                    <p>{v.name}</p>
                </CardContent>
            </Card>
        )
    }

    render() {


        return (
            <div>
                <h4>{this.card()}</h4>
            </div>
        );


    }
}

export default NearMensa
