            const loadData = async () => {
                const response = await fetch('/mygericht/')
                const data = await response.json()
                //console.log(data)
                setreadyLiebling(data)
                console.log({ readyLiebling })
                //console.log(this.state.readyLiebling[2].id + " testing")
