            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(g)
        };
        const response = await fetch('/mygericht', requestOptions);
        const data = await response.json();
        console.log(data)
    }

    const isLiebling = () => {
        const g = props.g
        const myLiebling = { readyLiebling }
