import React, { Component } from "react";
import { Card } from "@material-ui/core";
import CardContent from '@material-ui/core/CardContent';

const localitation = () =>{
    var dataN = []
    if ("geolocation" in navigator) {
        navigator.geolocation.getCurrentPosition(async function (position) {

            var url = 'http://openmensa.org/api/v2/canteens?near[lat]=' + position.coords.latitude + '&near[lng]=' + position.coords.longitude + '&near[dist]=5';
            const response = await fetch(url);
            const data = await response.json()
            dataN.push(data[0])
            

        });
        
    } return dataN;
}

class NearMensa extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: NaN,
            isLoaded: false,
            url: ""
        };
    }

    card() {
        const v = localitation()
        console.log(v.id[0])
        return (
            <Card>
                <CardContent>
                    <p>{v.name}</p>
                </CardContent>
            </Card>
        )
    }

    render() {


        return (
            <div>
                <h4>{this.card()}</h4>
            </div>
        );


    }
}

export default NearMensa
