import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import fetch from 'node-fetch'

const useStyles = makeStyles(theme => ({
    formControl: {
        margin: theme.spacing(8),
        minWidth: 332
    },
    selectEmpty: {
        marginTop: theme.spacing(2)
    }
}));

export default function MensaSelect() {
    const classes = useStyles();
    const [state, setState] = React.useState({
        age: "",
        name: "hai"
    });

    const useDataFetch = url => {
        const [data, setData] = useState(null);
        useEffect(() => {
          const loadData = async url => {
            const response = await fetch(url);
            const json = await response.json();
            setData(json);
          };
          loadData(url);
        }, [url]);
        return data;
      };



    const handleChange = event => {
        const name = event.target.name;
        setState({
            ...state,
            [name]: event.target.value
        });
    };

    return (
        <div>
            <FormControl required className={classes.formControl}>
                <InputLabel htmlFor="age-native-required">Lieblingsmensa ausw√§hlen</InputLabel>
                <Select
                    native
                    value={state.age}
                    onChange={handleChange}
                    name="age"
                    inputProps={{
                        id: "age-native-required"
                    }}
                >
                    <option aria-label="None" value="" />
                    {(() => {
                        const options = [<option value={1}>{8}</option>];
                        let url = '/mensen';
                        fetch(url)
                            .then(res => res.json())
                            .then((out) => {
                                console.log(out)
                                for (var i in out) {
                                    var name2 = JSON.stringify(out[i].name);
                                    console.log(name2);
                                    options.push(<option value={name2}>{name2}</option>);
                                } 
                            });


                        return options;
                    })()}
                </Select>
            </FormControl>
        </div>
    );
}
