import React, { useEffect } from "react";
import MensaSelect from './SelectMensa';
import Button from '@material-ui/core/Button';
import displayMyMensa from './displayMyMensa'

export default class MeineMensa extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            mensaName: null,
            data: null,
            liebling: [],
            isLoaded: false
        };
    }


    //https://www.robinwieruch.de/react-fetching-data
    componentWillMount() {
        fetch('/mensen')
            .then(response => response.json())
            .then(data => this.setState({ data }));
        fetch('/mymensa')
            .then(response => response.json())
            .then(liebling => this.setState({ liebling, isLoaded: true }));
    }

    selecting = (event) => {
        this.setState({ mensaName: event.target.value })
    }

    postrequest = () => fetch('/mymensa', {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(this.getData())
    })

    getData() {
        var dataM = {}
        for (var x in this.state.data) {
            if (this.state.data[x].name === this.state.mensaName) {
                dataM = this.state.data[x]
            }

        }
        return dataM
    }

    alertF = () => {
        this.postrequest()
    }

    render() {
        const { liebling, isLoaded } = this.state
        return (

            <div>
                {(isLoaded) ?
                    <p>Loading Country Names...</p> :
                    (!liebling.length) ?
                        <p>No country Names</p> :
                        <ul>
                            {liebling.map((x, i) => <li key={i}>{x}</li>)}
                        </ul>}
            </div>

        );
    }
}
