import React, { Component } from "react";

class NearMensa extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            isLoaded: false
        };
    }

    async componentDidMount() {

        var dataN = []
        var url = ""
        if ("geolocation" in navigator) {
            navigator.geolocation.getCurrentPosition(function (position) {
                
                url = 'openmensa.org/api/v2/canteens?near[lat]=' + position.coords.latitude + '&near[lng]=' + position.coords.longitude + '&near[dist]=5';
                console.log(url)
            
            });
        } else {
            console.log("Not Available");
        }
        const response = await fetch(url);
        console.log(response)
        const data = await response.json();
        
        console.log(data[0] + "wait")
        dataN = data;
        this.setState({ data: dataN })
        this.setState({ isLoaded: true })
    }

    render() {
        const { data, isLoaded } = this.state;

        if (isLoaded) {
            return (
                <div>
                    <h4>{data[0].name}</h4>
                </div>
            );
        } else {
            return <h2>Position k√∂nnte nicht ermittel...</h2>
        }

    }
}

export default NearMensa
