import React from "react";
import MensaSelect from './SelectMensa';
import Button from '@material-ui/core/Button';
import UseDataFetch from './FetchMensa';
import { useState, useEffect } from 'react';

function setMyMensa(allemensa){
    for (var i = 0; i < allemensa.lenght; i++) {
        if (allemensa[i].name === 'mensaName') {
            var mymensa = allemensa[i]
            return mymensa;
        }
    }
}


export default function MeineMensa() {
    const [mensaName, setmensaName] = React.useState("")
    const [allemensa, setAll] = useState({})

    useEffect (() => {
        setAll(UseDataFetch('/mensen'))
    })
    
    var selectedMensa = setMyMensa(allemensa)


    alert(selectedMensa.name)



    const selecting = (event) => {
        setmensaName(event.target.value)
        console.log(allemensa)
    }

    const alertF = () => {
        alert(mensaName)


    }

    return (
        <div>
            <h2>Meine Lieblingsmensa</h2>
            <hr id='linia'></hr>
            <p>{mensaName}</p>


            <h1>Mensa ausw√§hlen</h1>
            <hr id='linia'></hr>
            <MensaSelect value={mensaName} select={selecting} />
            <Button onClick={alertF}>LOL</Button>
        </div>
    );
}
