        super(props);
        this.state = {
            mensaName: null,
            data: null,
            liebling: [],
            isLoaded: false
        };
    }


    //https://www.robinwieruch.de/react-fetching-data
    componentWillMount() {
        this.setState({ isLoaded: true })
        fetch('/mensen')
            .then(response => response.json())
            .then(data => this.setState({ data }));
    }

    selecting = (event) => {
        this.setState({ mensaName: event.target.value })
    }

