import React, { Component } from "react";

class NearMensa extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: []
        };
    }

    componentDidMount() {
        var dataN = []
        if ("geolocation" in navigator) {
            navigator.geolocation.getCurrentPosition(function (position) {
                const url = 'openmensa.org/api/v2/canteens?near[lat]='+position.coords.latitude+'&near[lng]='+position.coords.longitude+'&near[dist]=5';
                const response = fetch(url);
                const data = response.json();
                dataN = data
            });
        } else {
            console.log("Not Available");
        }
        this.setState({data: dataN})
    }

    render() {
        return (
            <div>
                <h4>{this.state.data[0].name}</h4>
            </div>
        );
    }
}

export default NearMensa
