import UseDataFetch from './FetchMensa';
import React from "react";

const settingM = async() => {
    const data = UseDataFetch('/mensen');
    var options = [];
    const postTitle = () => {
        if (data) {
            for (var i in data) {
                options.push(i);
            }
            return options[0].name;
        } else {
            return <h2>Daten werden geladen...</h2>;
        }
    };

    return (
        postTitle()
    );
}
export default settingM
