import React from "react";
import MensaSelect from './SelectMensa';
import Button from '@material-ui/core/Button';
import UseDataFetch from './FetchMensa';
import { useState, useEffect } from 'react';

export default function MeineMensa() {
    const [mensaName, setmensaName] = React.useState("")
    var mymensa = []
    const allemensa = getMensas()
    async function getMensas(){
        var data = await UseDataFetch('/mensen')
        return data
    }

    const selecting = (event) => {
        setmensaName(event.target.value)
        for(var x in allemensa){
            console.log(x.name)
            if(x.name == mensaName){
                mymensa = x
                alert(mymensa.id)
            }
        }
    }

    const alertF = () => {
        alert(mensaName)
        alert(mymensa.id)


    }

    return (
        <div>
            <h2>Meine Lieblingsmensa</h2>
            <hr id='linia'></hr>
            <p>{mensaName}</p>


            <h1>Mensa ausw√§hlen</h1>
            <hr id='linia'></hr>
            <MensaSelect value={mensaName} select={selecting} />
            <Button onClick={alertF}>LOL</Button>
        </div>
    );
}
