import React, { Component } from 'react';

class TimeHome extends Component {
    constructor(props) {
        super(props);
        this.state = {
            date: [],
            mymensa: "",
            isLoaded: false
        };
    }

    componentWillMount() {
        this.setState({ isLoaded: true })
        
        fetch('/mymensa')
            .then(response => response.json())
            .then(mymensa => this.setState({ mymensa }));
        let url = new URL('https://openmensa.org/api/v2/canteens/:mensa/days/:date')
        url.search = new URLSearchParams({
            mensa:this.state.mymensa,
            date: '2020-07-04'
        })
        fetch(url)
            .then(response => response.json())
            .then(date => this.setState({ date }));
    }
    render() {
        const {date, isLoaded} = this.state
        return (
            <React.Fragment>
                {
                    (isLoaded) ?
                        date.map(titel => {
                            const { date } = titel;
                            return (
                                <div>
                                    <h2>{date}</h2>
                                    
                                </div>
                            )
                        }

                        ) : <p>no test</p>
                }

            </React.Fragment>
        )
    }
}
export default TimeHome
