import React from "react";
import MensaSelect from './SelectMensa';
import Button from '@material-ui/core/Button';
import { withStyles } from '@material-ui/core/styles'

const styles = theme => ({
    root: {
      background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
      border: 0,
      borderRadius: 3,
      boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
      color: 'white',
      height: 48,
      padding: '0 30px',
      position: 'relative',
      marginTop: 100
    },
  });

class MeineMensa extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            mensaName: null,
            data: null,
            liebling: {},
            isLoaded: false
        };
    }


    //https://www.robinwieruch.de/react-fetching-data
    async componentWillMount() {
        this.setState({ isLoaded: true })

        const response = await fetch('/mensen');
        const mdata = await response.json();
        this.setState({ data: mdata });

        const response2 = await fetch('/mymensa');
        const mdata2 = await response2.json();

        this.setState({ liebling: mdata2[0] });

    }

    selecting = (event) => {
        this.setState({ mensaName: event.target.value })
    }

    /* postrequest = () => fetch('/mymensa', {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(this.getData())
        
    }) */

    postrequest() {
        // Simple POST request with a JSON body using fetch
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(this.getData())
        };
        fetch('/mymensa', requestOptions)
            .then(response => response.json())
            .then(data => console.log(data));
    }


    getData() {
        var dataM = {}

        for (var x in this.state.data) {
            if (this.state.data[x].name === this.state.mensaName) {
                dataM = this.state.data[x]
            }

        }
        return dataM
    }

    alertF = () => {
        this.postrequest()
        window.location.reload(false);
    }

    render() {
        const { liebling, isLoaded } = this.state
        const { classes } = this.props;
        if (isLoaded) {
            return (
                <div>
                    <div>
                        <h2 id='HomeTitle1'>Meine Lieblingsmensa</h2>
                        <p id='HomeTitle2'>{liebling.name}</p>
                        <h2 id='HomeTitle1'>Mensa ausw√§hlen</h2>
                        <hr id='line'></hr>
                    </div>
                    <div>
                        <MensaSelect value={this.state.mensaName} select={this.selecting} />
                        <Button onClick={this.alertF} className={classes.root}>Speichern</Button>
                        <hr id='line'></hr>
                    </div>
                </div>

            )
        } else {
            return <p>sdfsdf</p>
        }


    }
}
export default withStyles(styles)(MeineMensa);
