import React, { Component } from "react";

class NearMensa extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            isLoaded: false,
            url: ""
        };
    }

    componentDidMount() {

        var dataN = []
        var url = ""
        if ("geolocation" in navigator) {
            navigator.geolocation.getCurrentPosition(async function (position) {
                
                url = 'http://openmensa.org/api/v2/canteens?near[lat]=' + position.coords.latitude + '&near[lng]=' + position.coords.longitude + '&near[dist]=5';
                const response = await fetch(url);
                const data = await response.json()
                console.log(data[0])
            });
        } else {
            console.log("Not Available");
        }
        this.setState({url: url})
        
    }

    render() {
        const { data, isLoaded } = this.state;

        if (isLoaded) {
            return (
                <div>
                    <h4>{data[0].name}</h4>
                </div>
            );
        } else {
            return <h2>Position k√∂nnte nicht ermittel...</h2>
        }

    }
}

export default NearMensa
