import React, { useEffect } from "react";
import MensaSelect from './SelectMensa';
import Button from '@material-ui/core/Button';
import PostDataMyMensa from './PostMyMensa'

export default class MeineMensa extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            mensaName: null,
            data: null,
            liebling: [],
            isLoaded: false
        };
    }


    //https://www.robinwieruch.de/react-fetching-data
    componentDidMount() {
        fetch('/mensen')
            .then(response => response.json())
            .then(data => this.setState({ data }));
        fetch('/mymensa')
            .then(response => response.json())
            .then(data => this.setState({ liebling: data, isLoaded: true }));
    }

    selecting = (event) => {
        this.setState({ mensaName: event.target.value })
    }

    postrequest = () => fetch('/mymensa', {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(this.getData())
    })

    getData() {
        var dataM = {}
        for (var x in this.state.data) {
            if (this.state.data[x].name === this.state.mensaName) {
                dataM = this.state.data[x]
            }

        }
        return dataM
    }

    alertF = () => {
        this.postrequest()
    }

    render() {
        const isLoaded = this.state.isLoaded
        const {mensaLieb} = this.state
        
        if (!isLoaded) {
            return (

                <div>
                    <h2>Meine Lieblingsmensa</h2>
                    <hr id='linia'></hr>
                    <p>Keine Lieblingsmensa gespeichert...</p>

                    <h1>Mensa auswählen</h1>
                    <hr id='linia'></hr>
                    <MensaSelect value={this.state.mensaName} select={this.selecting} />
                    <Button onClick={this.alertF}>Speichern</Button>
                </div >
            )

        } else {
            return (
                
                <div>
                    <h2>Meine Lieblingsmensa</h2>
                    <hr id='linia'></hr>
                    
                    <p>{mensaLieb}</p>

                    <h1>Mensa auswählen</h1>
                    <hr id='linia'></hr>
                    <MensaSelect value={this.state.mensaName} select={this.selecting} />
                    <Button onClick={this.alertF}>Speichern</Button>

                </div>)

        }





    }
}
