import MensaSelect from './SelectMensa';
import React from 'react';
import UseDataFetch from './FetchMensa'

class MensenSicht extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            mensaName: "",
            data: []
        };
    }
    

    selecting = (event) => {
        this.setState({ mensaName: event.target.value })
        const name = this.state.mensaName
        const url = '/mensen/' + name
        const data = UseDataFetch(url)
        this.setState({data: data})
    }

    render(){
        
    return (
