import React, { Component } from "react";

class NearMensa extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: []
        };
    }

    async componentDidMount() {
        var dataN = []
        var url = ""
        if ("geolocation" in navigator) {
            navigator.geolocation.getCurrentPosition(function (position) {
                url = 'openmensa.org/api/v2/canteens?near[lat]=' + position.coords.latitude + '&near[lng]=' + position.coords.longitude + '&near[dist]=5';
            });
        } else {
            console.log("Not Available");
        }
        const response = await fetch(url);
        const data = await response.json();
        dataN = data;
        this.setState({ data: dataN })
    }

    render() {
        return (
            <div>
                <h4>{this.state.data[0].name}</h4>
            </div>
        );
    }
}

export default NearMensa
