import React, { Component } from "react";
import { Card } from "@material-ui/core";
import CardContent from '@material-ui/core/CardContent';

class NearMensa extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: NaN,
            isLoaded: false,
            url: ""
        };
    }

    localitation() {
        
        if ("geolocation" in navigator) {
            navigator.geolocation.getCurrentPosition(async function (position) {
                
                var url = 'http://openmensa.org/api/v2/canteens?near[lat]=' + position.coords.latitude + '&near[lng]=' + position.coords.longitude + '&near[dist]=5';
                const response = await fetch(url);
                const data = await response.json()
                const dataN = data[0]
                return dataN
                
            });
        } else {
            return null;
        }
    }

    card(){
        return(
            <Card>
                <CardContent>
                    <p>{this.localitation().name}</p>
                </CardContent>
            </Card>
        )
    }

    render() {
        const data = this.localitation()

        if (data != null ) {
            return (
                <div>
                    <h4>{data}</h4>
                </div>
            );
        } else {
            return <h2>Position k√∂nnte nicht ermittel...</h2>
        }

    }
}

export default NearMensa
