import React from "react";
import MensaSelect from './SelectMensa';
import Button from '@material-ui/core/Button';
import settingM from './settingMyMensa';
import { useState, useEffect } from 'react';

export default class MeineMensa extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            mensaName: null,
            data: null,
        };
    }


    //https://www.robinwieruch.de/react-fetching-data
    componentDidMount() {
        fetch('/mensen')
            .then(response => response.json())
            .then(data => this.setState({ data }));
    }

    selecting = (event) => {
        this.setState({ mensaName: event.target.value })
    }

    alertF = () => {
        for (var x in this.state.data) {
            console.log(x)
            if (x.name == this.state.mensaName)
                alert(this.state.data.name)
        }
    }

    render() {
        return (

            <div>
                <h2>Meine Lieblingsmensa</h2>
                <hr id='linia'></hr>
                <p>{this.state.mensaName}</p>


                <h1>Mensa ausw√§hlen</h1>
                <hr id='linia'></hr>
                <MensaSelect value={this.state.mensaName} select={this.selecting} />
                <Button onClick={this.alertF}>LOL</Button>
            </div>

        );
    }
}
