import React, { Component } from 'react';

function FormatDate(date) {
    var d = new Date(date),
        month = '' + (d.getMonth() + 1),
        day = '' + d.getDate(),
        year = d.getFullYear();

    if (month.length < 2)
        month = '0' + month;
    if (day.length < 2)
        day = '0' + day;

    return [year, month, day].join('-');
}

class TimeHome extends Component {
    constructor(props) {
        super(props);
        this.state = {
            mydate: [],
            mymensa: "",
            isLoaded: false
        };
    }



    componentWillMount() {
        this.setState({ isLoaded: true })
        //https://stackoverflow.com/questions/40981040/using-a-fetch-inside-another-fetch-in-javascript
        fetch('/mymensa', {
            method: 'get',
        }).then(function (response) {
            return response.json(); // pass the data as promise to next then block
        }).then(function (data) {
            var canteen_id = data[0].id;
            var date = new Date('2019,10,11')
            let day_date = FormatDate(date)


            console.log(canteen_id, '\n' + day_date);
            var url = 'https://openmensa.org/api/v2/canteens/' + canteen_id + '/days/' + day_date
            return fetch(url); // make a 2nd request and return a promise
        })
            .then(response => response.json())
            .then(mydate => this.setState({ mydate }))
            .catch(function (error) {
                this.setState({ isLoaded: false })
            })




    }
    render() {
        const { mydate, isLoaded } = this.state
        return (
            <React.Fragment>
                {
                    (isLoaded) ?
                        mydate.map(titel => {
                            const { date } = titel;
                            return (
                                <div>
                                    <h2>{date}</h2>

                                </div>
                            )
                        }

                        ) : <p>no test</p>
                }

            </React.Fragment>
        )
    }
}
export default TimeHome
